import React, { Component } from 'react'
import PubSub from 'pubsub-js'

import "./index.css"
import Item from '../Item'

export default class List extends Component {

    state = {
        todos: [
            { id: "001", name: "eating2", done: true },
            { id: "002", name: "working2", done: false }
        ]
    }

    componentDidMount() {
        this.token = PubSub.subscribe("Search_value", (_, stateObj) => {
            const { todos } = this.state
            this.setState({ todos: [stateObj, ...todos] })
        })

        this.token = PubSub.subscribe("handleCheck", (_, stateObj) => {
            const { todos } = this.state

            const newTodos = todos.map(todo => {
                if (todo.id === stateObj.id) {
                    // todo.done = stateObj.done
                    // return todo
                    return {...todo,done:stateObj.done}
                }
                else{
                    return todo
                }
            })
            console.log(newTodos,"........")
            this.setState({todos:newTodos})

        })

    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.token)

    }


    render() {
        const { todos } = this.state
        console.log(todos)
        return (
            <ul className="todo-main">
                {
                    todos.map(todo => {
                        return <Item key={todo.id} {...todo} />
                    })
                }


            </ul>
        )
    }
}
