import React, { Component } from 'react'
import { connect } from 'react-redux'
import PubSub from 'pubsub-js'
import './index.css'


class Footer extends Component {

    handleClear = () =>{
        PubSub.publish("handleClear","click")
    }

    finishedFunction = () =>{
       let new_c =  this.props.todos.reduce((per,cur)=>{
            if(!cur.done){
                return per + 1
            }
        },0)
        return new_c

    }

    render() {
        return (
            <div className="todo-footer">
                <label>
                    <input type="checkbox"  />
                </label>
                <span><span onClick={this.finishedFunction}>finished  {alert(this.finishedFunction)}</span> / Total {this.props.todos.length} </span>
                <button onClick={this.handleClear} className="btn btn-danger">Clear finished tasks</button>
            </div>
        )
    }
}
export default connect(
    state =>({
        todos:state.todoReducer
    }) 
)(Footer)
